def findPacket(s):
    results = {}
    for i in range(len(s)):
        if i >= 3:
            if s[i] != s[i-1] and s[i] != s[i-2] and s[i] != s[i-3] and s[i-1] != s[i-2] and s[i-1] != s[i-3] and s[i-2] != s[i-3]:
                results["index"] = i+1
                results["value"] = s[i-3] + s[i-2] + s[i-1] + s[i]
                break
    print(results)
    return results

def findMarker(s):
    results = {}
    for i in range(len(s)):
        if i>=13:
            if len(set(s[i-14:i])) == 14:
                results["index"] = i
                break
    print(results)
    return results


